import { studentList } from '../data/studentList'
import ListElement from '../components/ListElement'
import default_photo from '../assets/profile.png'
import { Link } from 'react-router-dom'
import React, { useState } from 'react'

function GestionEleves() {
    const [students, setStudents] = useState(studentList)
    const handleDelete = (id) => {
        setStudents(students.filter((stud) => stud.id !== id))
    }
    return (
        <div className="">
            <div className="w-full  text-center">
                <div className=" flex  ax-w-32 max-h-32  overflow-scroll">
                    <table>
                        <thead>
                            <tr>
                                <th>Actions</th>
                                <th>Noms</th>
                                <th>Prénoms</th>
                                <th>sexe</th>
                                <th>Date début</th>
                                <th>Date fin</th>
                                <th>Reste</th>
                                <th>Formation</th>
                                <th>Montant</th>
                                <th>Avance</th>
                                <th>Niveau</th>
                                <th>Date Naissance</th>
                                <th>Contact</th>
                                <th>Quartier</th>
                                <th>Email</th>
                                <th>Photo</th>
                            </tr>
                        </thead>
                        <tbody>
                            {students.map((stud) => (
                                <ListElement
                                    key={stud.id}
                                    handleDelete={handleDelete}
                                    id={stud.id}
                                    name={stud.name}
                                    surname={stud.surname}
                                    sexe={stud.sexe}
                                    start={stud.start}
                                    end={stud.end}
                                    rest={stud.rest}
                                    training={stud.training}
                                    birthday={stud.birthday}
                                    level={stud.level}
                                    tel1={stud.tel1}
                                    amount={stud.amount}
                                    advance={stud.advance}
                                    quater={stud.quater}
                                    email={stud.email}
                                    photo={stud.photo ? stud.photo : default_photo}
                                />
                            ))}
                        </tbody>
                    </table>
                </div>

                <Link to="/NouvelEleve/InfosPerso" className="add-student-btn">
                    <div>
                        <span class="material-icons">add_circle</span>
                    </div>
                </Link>
            </div>
        </div>
    )
}

export default GestionEleves
