import { Link } from 'react-router-dom'
import logo from '../assets/digital-school-logo-transparent.png'
import { SIDE_BAR_TOP_LINKS } from '../utils/SideBar'
import classNames from 'classnames'
import { useLocation } from 'react-router-dom'

const LinkClasses = 'text-grey text-xl flex flex-row items-center gap-2 pl-5 py-3 hover:bg-light-blue  rounded-sm '

const ActiveLink = 'text-white bg-blue'

function SideBar() {
    return (
        <div className="bg-white  w-60 py-3 px-1 flex flex-col shadow-xl">
            <div className="flex items-center  mx-auto pb-4">
                <img src={logo} alt="logo" className="object-contain  w-40" />
            </div>
            <div className="flex-1 flex-col gap-0.5">
                {SIDE_BAR_TOP_LINKS.map((item) => (
                    <SideBarLink key={item.label} item={item} />
                ))}
            </div>
            <div></div>
            SideBar
        </div>
    )
}

export default SideBar

function SideBarLink({ item }) {
    const location = useLocation()
    return (
        <Link to={item.path} className={classNames(location.pathname === item.path ? ActiveLink : '', LinkClasses)}>
            <span>{item.icon}</span>
            {item.label}
        </Link>
    )
}
