import { studentList } from '../data/studentList'
import ListElement from '../components/ListElement'
import default_photo from '../assets/profile.png'
import { Link } from 'react-router-dom'
import React, { useState } from 'react'

function GestionEleves() {
    const thStyle = 'pl-3 whitespace-nowrap py-4'
    const [students, setStudents] = useState(studentList)
    const handleDelete = (id) => {
        setStudents(students.filter((stud) => stud.id !== id))
    }
    return (
        <div className="flex flex-col items-center justify-center w-full h-full ">
            <div className=" mt-1 pt-2 flex h-[500px] w-[1000px] overflow-scroll">
                <table>
                    <thead>
                        <tr className="bg-blue text-white  px-2 text-left rounded-md">
                            <th className={thStyle}>Actions</th>
                            <th className={thStyle}>Noms</th>
                            <th className={thStyle}>Prénoms</th>
                            <th className={thStyle}>sexe</th>
                            <th className={thStyle}>Date début</th>
                            <th className={thStyle}>Date fin</th>
                            <th className={thStyle}>Reste</th>
                            <th className={thStyle}>Formation</th>
                            <th className={thStyle}>Montant</th>
                            <th className={thStyle}>Avance</th>
                            <th className={thStyle}>Niveau</th>
                            <th className={thStyle}>Date Naiss.</th>
                            <th className={thStyle}>Contact</th>
                            <th className={thStyle}>Quartier</th>
                            <th className={thStyle}>Email</th>
                            <th className={thStyle}>Photo</th>
                        </tr>
                    </thead>
                    <tbody>
                        {students.map((stud) => (
                            <ListElement
                                key={stud.id}
                                handleDelete={handleDelete}
                                id={stud.id}
                                name={stud.name}
                                surname={stud.surname}
                                sexe={stud.sexe}
                                start={stud.start}
                                end={stud.end}
                                rest={stud.rest}
                                training={stud.training}
                                birthday={stud.birthday}
                                level={stud.level}
                                tel1={stud.tel1}
                                amount={stud.amount}
                                advance={stud.advance}
                                quater={stud.quater}
                                email={stud.email}
                                photo={stud.photo ? stud.photo : default_photo}
                            />
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="flex ">
                <Link to="/NouvelEleve/InfosPerso" className="add-student-btn">
                    <div>
                        <span className="material-icons text-blue text-[40px] mt-2">add_circle</span>
                    </div>
                </Link>
            </div>
        </div>
    )
}

export default GestionEleves
