import { Link } from 'react-router-dom'
import logo from '../assets/digital-school-logo-transparent.png'
import { SIDE_BAR_TOP_LINKS } from '../utils/SideBar'

const LinkClasses =
    'text-grey text-xl flex flex-row items-center gap-2 px-2 py-3 hover:bg-light-blue active:bg-blue active:text-white rounded-sm '
function SideBar() {
    return (
        <div className="bg-white  w-60 p-3 flex flex-col shadow-xl">
            <div className="flex items-center   mx-auto">
                <img src={logo} alt="logo" className="object-contain  w-40" />
            </div>
            <div className="flex-1">
                {SIDE_BAR_TOP_LINKS.map((item) => (
                    <SideBarLink key={item.label} item={item} />
                ))}
            </div>
            <div></div>
            SideBar
        </div>
    )
}

export default SideBar

function SideBarLink({ item }) {
    return (
        <Link to={item.path} className={LinkClasses}>
            <span>{item.icon}</span>
            {item.label}
        </Link>
    )
}
